// Application state
let currentState = {
  currentView: 'homepage',
  currentChapterId: null,
  theme: 'light',
  fontSize: 'normal',
  bookmarkedChapters: new Set(),
  readingProgress: {}
};

// DOM elements - get them when needed to avoid null references
function getElements() {
  return {
    homepage: document.getElementById('homepage'),
    chapterView: document.getElementById('chapterView'),
    chaptersGrid: document.getElementById('chaptersGrid'),
    chapterTitle: document.getElementById('chapterTitle'),
    chapterDescription: document.getElementById('chapterDescription'),
    chapterContent: document.getElementById('chapterContent'),
    backToChapters: document.getElementById('backToChapters'),
    prevChapter: document.getElementById('prevChapter'),
    nextChapter: document.getElementById('nextChapter'),
    currentChapter: document.getElementById('currentChapter'),
    totalChapters: document.getElementById('totalChapters'),
    themeToggle: document.getElementById('themeToggle'),
    fontSizeToggle: document.getElementById('fontSizeToggle'),
    bookmarkChapter: document.getElementById('bookmarkChapter'),
    loading: document.getElementById('loading'),
    bookmarkNotification: document.getElementById('bookmarkNotification'),
    logo: document.getElementById('logo')
  };
}

// Initialize application
function initializeApp() {
  renderChaptersGrid();
  setupEventListeners();
  updateTotalChapters();
}

// Render chapters grid on homepage
function renderChaptersGrid() {
  const elements = getElements();
  if (!elements.chaptersGrid) return;
  
  elements.chaptersGrid.innerHTML = '';
  
  chaptersData.chapters.forEach(chapter => {
    const chapterCard = document.createElement('div');
    chapterCard.className = 'chapter-card';
    chapterCard.setAttribute('data-chapter-id', chapter.id);
    chapterCard.style.cursor = 'pointer';
    
    const isBookmarked = currentState.bookmarkedChapters.has(chapter.id);
    const bookmarkIcon = isBookmarked ? '🔖' : '';
    
    chapterCard.innerHTML = `
      <div class="chapter-card-content">
        <div class="chapter-number">Chapter ${chapter.id} ${bookmarkIcon}</div>
        <h4 class="chapter-card-title">${chapter.title}</h4>
        <p class="chapter-card-description">${chapter.description}</p>
      </div>
    `;
    
    // Add click event listener directly to each card
    chapterCard.addEventListener('click', function(e) {
      e.preventDefault();
      e.stopPropagation();
      loadChapter(chapter.id);
    });
    
    elements.chaptersGrid.appendChild(chapterCard);
  });
}

// Load and display a specific chapter
function loadChapter(chapterId) {
  const elements = getElements();
  showLoading();
  
  setTimeout(() => {
    const chapter = chaptersData.chapters.find(ch => ch.id === chapterId);
    if (!chapter) {
      hideLoading();
      return;
    }
    
    currentState.currentChapterId = chapterId;
    
    // Update chapter content
    if (elements.chapterTitle) elements.chapterTitle.textContent = chapter.title;
    if (elements.chapterDescription) elements.chapterDescription.textContent = chapter.description;
    
    // Format chapter content with paragraphs
    const formattedContent = chapter.content.split('\n\n').map(paragraph => 
      `<p>${paragraph.trim()}</p>`
    ).join('');
    if (elements.chapterContent) elements.chapterContent.innerHTML = formattedContent;
    
    // Update navigation
    updateChapterNavigation(chapterId);
    
    // Update bookmark button
    updateBookmarkButton(chapterId);
    
    // Switch to chapter view
    switchView('chapterView');
    
    // Mark as read
    currentState.readingProgress[chapterId] = Date.now();
    
    hideLoading();
    
    // Scroll to top
    window.scrollTo({ top: 0, behavior: 'smooth' });
  }, 800);
}

// Update bookmark button state
function updateBookmarkButton(chapterId) {
  const elements = getElements();
  if (!elements.bookmarkChapter) return;
  
  const isBookmarked = currentState.bookmarkedChapters.has(chapterId);
  elements.bookmarkChapter.textContent = isBookmarked ? '⭐ Bookmarked' : '🔖 Bookmark';
}

// Update chapter navigation buttons
function updateChapterNavigation(chapterId) {
  const elements = getElements();
  const currentIndex = chaptersData.chapters.findIndex(ch => ch.id === chapterId);
  
  // Update current chapter display
  if (elements.currentChapter) elements.currentChapter.textContent = chapterId;
  
  // Update previous button
  if (elements.prevChapter) {
    if (currentIndex > 0) {
      elements.prevChapter.disabled = false;
      elements.prevChapter.onclick = function() {
        loadChapter(chaptersData.chapters[currentIndex - 1].id);
      };
    } else {
      elements.prevChapter.disabled = true;
      elements.prevChapter.onclick = null;
    }
  }
  
  // Update next button
  if (elements.nextChapter) {
    if (currentIndex < chaptersData.chapters.length - 1) {
      elements.nextChapter.disabled = false;
      elements.nextChapter.onclick = function() {
        loadChapter(chaptersData.chapters[currentIndex + 1].id);
      };
    } else {
      elements.nextChapter.disabled = true;
      elements.nextChapter.onclick = null;
    }
  }
}

// Switch between views
function switchView(viewName) {
  const elements = getElements();
  
  // Hide all views
  if (elements.homepage) elements.homepage.classList.remove('active');
  if (elements.chapterView) elements.chapterView.classList.remove('active');
  
  // Show target view
  if (elements[viewName]) {
    elements[viewName].classList.add('active');
  }
  
  currentState.currentView = viewName;
}

// Setup event listeners
function setupEventListeners() {
  const elements = getElements();
  
  // Back to chapters button
  if (elements.backToChapters) {
    elements.backToChapters.addEventListener('click', function() {
      switchView('homepage');
      renderChaptersGrid(); // Refresh to show bookmarks
    });
  }
  
  // Theme toggle
  if (elements.themeToggle) {
    elements.themeToggle.addEventListener('click', toggleTheme);
  }
  
  // Font size toggle
  if (elements.fontSizeToggle) {
    elements.fontSizeToggle.addEventListener('click', toggleFontSize);
  }
  
  // Bookmark chapter
  if (elements.bookmarkChapter) {
    elements.bookmarkChapter.addEventListener('click', toggleBookmark);
  }
  
  // Logo click returns to homepage
  if (elements.logo) {
    elements.logo.addEventListener('click', function() {
      if (currentState.currentView !== 'homepage') {
        switchView('homepage');
        renderChaptersGrid();
      }
    });
  }
  
  // Keyboard navigation
  document.addEventListener('keydown', handleKeyNavigation);
}

// Handle keyboard navigation
function handleKeyNavigation(event) {
  const elements = getElements();
  
  if (currentState.currentView === 'chapterView') {
    switch(event.key) {
      case 'ArrowLeft':
        if (elements.prevChapter && !elements.prevChapter.disabled) {
          elements.prevChapter.click();
        }
        break;
      case 'ArrowRight':
        if (elements.nextChapter && !elements.nextChapter.disabled) {
          elements.nextChapter.click();
        }
        break;
      case 'Escape':
        if (elements.backToChapters) {
          elements.backToChapters.click();
        }
        break;
    }
  }
}

// Toggle theme
function toggleTheme() {
  const elements = getElements();
  const body = document.body;
  const isCurrentlyLight = currentState.theme === 'light';
  
  if (isCurrentlyLight) {
    body.setAttribute('data-theme', 'dark');
    currentState.theme = 'dark';
    if (elements.themeToggle) elements.themeToggle.textContent = '☀️ Light Mode';
  } else {
    body.removeAttribute('data-theme');
    currentState.theme = 'light';
    if (elements.themeToggle) elements.themeToggle.textContent = '🌙 Dark Mode';
  }
}

// Toggle font size
function toggleFontSize() {
  const elements = getElements();
  const chapterContent = elements.chapterContent;
  const isCurrentlyNormal = currentState.fontSize === 'normal';
  
  if (chapterContent) {
    if (isCurrentlyNormal) {
      chapterContent.classList.add('large-font');
      currentState.fontSize = 'large';
      if (elements.fontSizeToggle) elements.fontSizeToggle.textContent = 'Aa Normal Size';
    } else {
      chapterContent.classList.remove('large-font');
      currentState.fontSize = 'normal';
      if (elements.fontSizeToggle) elements.fontSizeToggle.textContent = 'Aa Font Size';
    }
  }
}

// Toggle bookmark for current chapter
function toggleBookmark() {
  const elements = getElements();
  if (!currentState.currentChapterId) return;
  
  const chapterId = currentState.currentChapterId;
  const isBookmarked = currentState.bookmarkedChapters.has(chapterId);
  
  if (isBookmarked) {
    currentState.bookmarkedChapters.delete(chapterId);
    if (elements.bookmarkChapter) elements.bookmarkChapter.textContent = '🔖 Bookmark';
    showNotification('Bookmark removed');
  } else {
    currentState.bookmarkedChapters.add(chapterId);
    if (elements.bookmarkChapter) elements.bookmarkChapter.textContent = '⭐ Bookmarked';
    showNotification('Chapter bookmarked! ⭐');
  }
}

// Update total chapters display
function updateTotalChapters() {
  const elements = getElements();
  if (elements.totalChapters) {
    elements.totalChapters.textContent = chaptersData.chapters.length;
  }
}

// Show loading screen
function showLoading() {
  const elements = getElements();
  if (elements.loading) {
    elements.loading.classList.remove('hidden');
  }
}

// Hide loading screen
function hideLoading() {
  const elements = getElements();
  if (elements.loading) {
    elements.loading.classList.add('hidden');
  }
}

// Show notification
function showNotification(message) {
  const elements = getElements();
  const notification = elements.bookmarkNotification;
  
  if (notification) {
    const messageElement = notification.querySelector('p');
    if (messageElement) messageElement.textContent = message;
    
    notification.classList.remove('hidden');
    notification.classList.add('show');
    
    setTimeout(() => {
      notification.classList.remove('show');
      setTimeout(() => {
        notification.classList.add('hidden');
      }, 300);
    }, 2000);
  }
}

// Add some dramatic flair with random color shifts (JoJo style)
function addJojoFlair() {
  const cards = document.querySelectorAll('.chapter-card');
  
  cards.forEach((card, index) => {
    card.addEventListener('mouseenter', () => {
      // Random slight color variations on hover
      const hue = (index * 60) % 360;
      card.style.filter = `hue-rotate(${hue}deg) brightness(1.1)`;
    });
    
    card.addEventListener('mouseleave', () => {
      card.style.filter = '';
    });
  });
}

// Enhanced initialization with JoJo flair
function initWithFlair() {
  // Wait for DOM to be fully ready
  setTimeout(() => {
    initializeApp();
    addJojoFlair();
  }, 100);
  
  // Add some periodic flair effects
  setInterval(() => {
    const elements = getElements();
    const logo = elements.logo;
    if (logo && currentState.currentView === 'homepage') {
      logo.style.transform = 'skew(-5deg) scale(1.05)';
      setTimeout(() => {
        logo.style.transform = 'skew(-5deg) scale(1)';
      }, 200);
    }
  }, 5000);
}

// Initialize the application when DOM is loaded
if (document.readyState === 'loading') {
  document.addEventListener('DOMContentLoaded', initWithFlair);
} else {
  initWithFlair();
}